import styled from "@emotion/styled";

export const Container = styled.div`
  margin-bottom: 45px;
`;

export const Results = styled.p`
  margin-bottom: 5px;

  font-weight: 600;
  font-size: 16px;
  line-height: 1.25;
`;

export const Line = styled.div`
  border: 1px solid $border;
`;
